void brentD_1_MLP_21_32_2_ScaleInputs(double* input, double minimum, double maximum, int size)
{
 double delta;
 long i;
 for(i=0; i<size; i++)
 {
	delta = (maximum-minimum)/(brentD_1_MLP_21_32_2_max_input[i]-brentD_1_MLP_21_32_2_min_input[i]);
	input[i] = minimum - delta*brentD_1_MLP_21_32_2_min_input[i]+ delta*input[i];
 }
}

void brentD_1_MLP_21_32_2_Normalise(double out[],long length)
{
  long i, j;
  double sum = 0.0;
  for(i=0; i<length; i++)
  {
   if(out[i]>100)
   {
    out[i] = 1.0;
    j = i;
    for(i=0; i<length; i++)
    {
     if(i!=j) out[i] = 0.0;
    }
    break;
   }
   else out[i] = exp(out[i]);
  }
  for(i=0; i<length; i++)
  {
   sum += out[i];
  }
  for(i=0; i<length; i++)
  {
   out[i] = out[i]/sum;
  }
}

void brentD_1_MLP_21_32_2_ComputeFeedForwardSignals(double* MAT_INOUT,double* V_IN,double* V_OUT, double* V_BIAS,int size1,int size2,int layer)
{
  int row,col;
  for(row=0;row < size2; row++) 
    {
      V_OUT[row]=0.0;
      for(col=0;col<size1;col++)V_OUT[row]+=(*(MAT_INOUT+(row*size1)+col)*V_IN[col]);
      V_OUT[row]+=V_BIAS[row];
      if(layer==0) V_OUT[row] = tanh(V_OUT[row]);
      if(layer==1) V_OUT[row] = tanh(V_OUT[row]);
   }
}

void brentD_1_MLP_21_32_2_RunNeuralNet_Classification () 
{
  brentD_1_MLP_21_32_2_ComputeFeedForwardSignals((double*)brentD_1_MLP_21_32_2_input_hidden_weights,brentD_1_MLP_21_32_2_input,brentD_1_MLP_21_32_2_hidden,brentD_1_MLP_21_32_2_hidden_bias,21, 32,0);
  brentD_1_MLP_21_32_2_ComputeFeedForwardSignals((double*)brentD_1_MLP_21_32_2_hidden_output_wts,brentD_1_MLP_21_32_2_hidden,brentD_1_MLP_21_32_2_output,brentD_1_MLP_21_32_2_output_bias,32, 2,1);
}

